      if (!innerSub) {
        innerSub = createOperatorSubscriber(subscriber, undefined, () => {
          innerSub = null;
          isComplete && subscriber.complete();
        });
        innerFrom(project(outerValue, index++)).subscribe(innerSub);
      }
    },
    () => {
      isComplete = true;
      !innerSub && subscriber.complete();
    }
  )
);
});

