  () => EMPTY
: operate((source, subscriber) => {
    let seen = 0;
    source.subscribe(
      createOperatorSubscriber(subscriber, (value) => {
        // Increment the number of values we have seen,
        // then check it against the allowed count to see
        // if we are still letting values through.
